INSERT INTO public.lieux (id, description, image, lat, lng, nom, indice_id) VALUES (2, 'Le lac Blanc est un lac d''altitude du massif des Vosges. Il se situe sur le ban de la commune d''Orbey en région Grand Est, région historique d''Alsace. Altitude : 1055m. Superficie : 29ha', '', 48.126064, 7.0902085, 'Lac Blanc', 1);
INSERT INTO public.lieux (id, description, image, lat, lng, nom, indice_id) VALUES (4, 'Le viaduc de Millau est un pont à haubans franchissant la vallée du Tarn, dans le département de l''Aveyron, en France. Longueur totale : 2460m. Hauteur : 343m. Début de la construction 16 octobre 2001.', '', 44.077454, 3.022839, 'Viaduc de Millau', 3);
INSERT INTO public.lieux (id, description, image, lat, lng, nom, indice_id) VALUES (14, 'La tour Eiffel est une tour de fer puddlé de 324 mètres de hauteur située à Paris, à l’extrémité nord-ouest du parc du Champ-de-Mars en bordure de la Seine dans le 7ᵉ arrondissement. Début de la construction : 28 Janvier 1887.', '', 48.8584, 2.2945, 'Tour Eiffel', 13);
INSERT INTO public.lieux (id, description, image, lat, lng, nom, indice_id) VALUES (16, 'Dijon est une commune française située entre le bassin parisien et le bassin rhodanien, à 310 kilomètres au sud-est de Paris et 190 kilomètres au nord de Lyon.', '', 47.3167, 5.0167, 'Dijon', 15);
INSERT INTO public.lieux (id, description, image, lat, lng, nom, indice_id) VALUES (18, 'La place Stanislas est une place appartenant à un ensemble urbain classique situé à Nancy, dans la région Lorraine, en France, qui est inscrit au patrimoine mondial de l''UNESCO.', '', 48.693928, 6.182858, 'Place Stanislas', 17);
INSERT INTO public.lieux (id, description, image, lat, lng, nom, indice_id) VALUES (20, 'Le musée de la Bière labellisé « Musée de France » et inscrit au titre des Monuments historiques est situé au sein de la ville de Stenay.', '', 49.48905, 5.18727, 'Musée de la bière', 19);
INSERT INTO public.lieux (id, description, image, lat, lng, nom, indice_id) VALUES (22, 'Marseille est une commune du Sud-Est de la France, chef-lieu du département des Bouches-du-Rhône et de la région Provence-Alpes-Côte d''Azur.', '', 43.2965, 5.3698, 'Marseille', 21);
INSERT INTO public.lieux (id, description, image, lat, lng, nom, indice_id) VALUES (26, 'Les Pyrénées sont une chaîne montagneuse du sud-ouest de l''Europe. Elles s''étendent en longueur selon une direction est-ouest sur une distance approximative de 430 kilomètres depuis la mer Méditerranée jusqu''au golfe de Gascogne. ', '', 42.940495, -0.525782, 'Les Pyrénées', 25);
INSERT INTO public.lieux (id, description, image, lat, lng, nom, indice_id) VALUES (28, 'Coquelles est une commune française située dans le département du Pas-de-Calais en région Hauts-de-France.', '', 50.9346, 1.7988, 'Coquelles', 27);
INSERT INTO public.lieux (id, description, image, lat, lng, nom, indice_id) VALUES (24, 'Le Mont-Saint-Michel est une commune française située dans le département de la Manche en Normandie. Elle tire son nom de l''îlot rocheux consacré à saint Michel où s’élève aujourd’hui l’abbaye du Mont-Saint-Michel.', '', 48.636, -1.5121, 'Mont St Michel', 23);